import {Component, OnInit} from '@angular/core';
import {AuthService} from "../../../services/auth.service";
import {Router} from "@angular/router";

@Component({
  selector: 'app-menu',
  templateUrl: './menu.component.html'
})
export class MenuComponent{
  constructor(public service: AuthService,
              private router: Router) {
  }

    deslogar(){
      this.service.deslogar();
      this.router.navigate(['entrar'])
    }
}
<nav class="navbar navbar-expand-lg navbar-dark bg-dark fixed-top">
    <div class="container-fluid">
        <a class="navbar-brand" [routerLink]="['/home']">Portal Colaboradores</a>
        <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span class="navbar-toggler-icon"></span>
        </button>
        <div class="collapse navbar-collapse" id="navbarNav">
            <ul class="navbar-nav me-auto">
                <li class="nav-item" *ngIf="service.logado">
                    <a class="nav-link" [routerLink]="['/colaboradores/lista']">Colaboradores</a>
                </li>
                <li class="nav-item" *ngIf="service.logado">
                    <a class="nav-link" [routerLink]="['/usuarios/lista']">Usuários</a>
                </li>
            </ul>
            <ul class="navbar-nav">
                <li class="nav-item" *ngIf="service.logado">
                    <a class="btn nav-link" (click)="deslogar()">Sair</a>
                </li>
            </ul>
        </div>
    </div>
</nav>
